import Input from '../../UI/Input';
import classes from './MealItemForm.module.css';

function MealItemModule (props) {

    return (
        <form className={classes.form}>
            <Input label="Amount" input={{
                id: `${Math.random()}`,
                type: 'number',
                min: '1',
                max: '5',
                step: '1',
                defaultValue: '1'
            }} />
            <button>+ Add</button>
        </form>
    )
}

export default MealItemModule;
